import { createSlice } from '@reduxjs/toolkit'

export const counterSlice = createSlice({
    name: 'buy-fasion-box',
    initialState: {
        boxCount: 0,
        totalPrice: 0
    },
    reducers: {
      buyFasionBox: (state) => {
        state.boxCount += 10,
        state.totalPrice += 270
      }
    },
  })

  export const { buyFasionBox } = counterSlice.actions
