import axios from "axios";
import React, { useState, useEffect, useRef, } from "react";
import parse from "html-react-parser";
import DrawModal from '../draw-modal.js'
import { Button, Card } from 'react-bootstrap';
import './fashion-box.css'
// Redux
import { addBox, drawBox } from "../../redux/reducers/fashion-box/actions.js";
// useSelector 讀取 data
// useDispatch 操作 data
import { useSelector, useDispatch } from 'react-redux';

// 購買時尚箱並計算價錢
const BuyBox = () => {

    // (state) => state.{name}.{para}
    // const boxCount = useSelector((state) => state.buyFashionBox.boxCount);
    // const totalPrice = useSelector((state) => state.buyFashionBox.totalPrice);
    const dispatch = useDispatch();
    // const totalPrice = useSelector((state) => state.buyFasionBox.totalPrice)

    return (
        <Card className='market-box' style={{ width: '150px' }}>
            <Card.Body>
                <img src='http://gametsg.techbang.com/maplestory/icon_item/cd1d4ea695bbbd48cdb8642b57696cd6.png'
                    width="80px"
                    height="80px"
                ></img>
                <p>時尚隨機箱</p>
                <p><s style={{ color: 'gray' }}>300樂豆點</s><span style={{ color: 'red' }}><b>(10%)</b></span></p>
                <p>270樂豆點(10個)</p>
                <Button variant='success' style={{ width: '100%' }} onClick={() => dispatch(addBox())}>
                    購買
                </Button>
            </Card.Body>
        </Card>
    )
}

// 雙擊時尚箱 icon 開啟小視窗
const HandleClickFashionBox = () => {

    const boxCount = useSelector((state) => state.buyFashionBox.boxCount);
    const dispatch = useDispatch();

    // 決定小視窗要不要開啟
    const [show, setShow] = useState(false);
    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);

    const [showContent, setshowContent] = useState();
    const [showLinkText, setShowLinkText] = useState();

    // 雙擊開啟小視窗
    const doubleClickFashionBoxIcon = () => {
        if (boxCount > 0) {
            const htmlContent = `擁有<img src='http://gametsg.techbang.com/maplestory/icon_item/cd1d4ea695bbbd48cdb8642b57696cd6.png'
            ></img><span className='text-danger'>時尚隨機箱</span>的話我就可以換一種最新的造型現金道具給你喔！如何，要立即使用嗎？ 
            <br/>`;
            const reactElement = parse(htmlContent);
            setshowContent(reactElement);
            setShowLinkText('現在立即使用時尚隨機箱');
            handleShow();
        } else {
            console.log('cant draw!');
        }
    }

    // 抽抽
    const [itemUrlData, setItemUrl] = useState();
    // 渲染後取得一般獎項圖片的 url
    useEffect(() => {
        axios.get('http://127.0.0.1:5000/fashion-box-img-url')
            .then((response) => {
                // url list 存在 itemUrlData
                setItemUrl(response.data.item_info);
            })
    }, []);
    // 取得不存在獎項的 img url
    const notAwardList = ['時裝神奇剪刀', '超性能擴音器', '核心寶石1個交換券(不可交易)', '核心寶石1個交換券', '日拋隱形眼鏡',
        '通用高級染髮券', '通用高級護膚券', '通用高級整形券', '通用整形券',
        '通用高級美髮券', '通用美髮券', '透明耳環', '透明鞋子', '透明手套',
        '透明披風', '透明眼部裝飾', '透明面具', '透明帽'];
    // 儲存 api 抽出來的物品
    const [item, setItem] = useState(null);
    // 儲存目前中獎物品列表
    const [awardList, setAwardList] = useState([]);
    // 儲存目前總抽數
    const [count, setCount] = useState(0);    
    // 渲染後先執行抽獎動作一次 => 避免第一次抽獎拿不到資料顯示 Null
    useEffect(() => {
        axios.get('http://127.0.0.1:5000/draw-fashion-box')
            .then((response) => {
                // 不是 json 直接拿 response.data 
                setItem(response.data);
            })
    }, []);
    // 小視窗彈出來後按抽獎
    const handleDrawFashionBoxClick = () => {
        axios.get('http://127.0.0.1:5000/draw-fashion-box')
            .then((response) => {
                // 不是 json 直接拿可以 response.data 
                // 從 api 取得抽出的物品存在 item
                setItem(response.data);
            })

        if (boxCount > 0) {
            setCount(count + 1);
            dispatch(drawBox());
            let imgUrl = itemUrlData.find(img => img.item_name === item);
            // 新的時裝查不到圖片網址
            if (!imgUrl) { imgUrl = `[new]`; }
            const htmlContent = `如何？<img src='${imgUrl.item_url}'
        ></img><span className='text-danger'>${item}</span>拿到道具了嗎？不覺得很神奇嗎？下次再有
        <span className='text-primary'>時尚隨機箱卷</span>的時候找我吧！<br/>`;
            const reactElement = parse(htmlContent);
            setshowContent(reactElement);
            setShowLinkText('再抽一次');
            if (!notAwardList.includes(item)) {
                setAwardList(getAwardList => [...getAwardList, item]);
            }
        } else {
            console.log('cant draw!');
            handleClose();
        }
    }

    return (
        <>
            <div className="icon-img">
                <img src='http://gametsg.techbang.com/maplestory/icon_item/cd1d4ea695bbbd48cdb8642b57696cd6.png'
                    onDoubleClick={() => {
                        doubleClickFashionBoxIcon();
                    }}
                    style={{
                        cursor: 'pointer',
                        maxWidth: '100%',
                        verticalAlign: 'bottom'
                    }}
                    width="40px"
                    height="40px"
                />
                <span className='box-count-text'>
                    <b>{boxCount}</b>
                </span>
            </div>
            <DrawModal
                show={show}
                onHide={handleClose}
                showContent={showContent}
                showLinkText={showLinkText}
            />
        </>
    )
}

// api 接收從獎池抽出的物品
const DrawFashionBox = () => {
    const boxCount = useSelector((state) => state.buyFashionBox.boxCount);
    const dispatch = useDispatch();

    const [itemUrlData, setItemUrl] = useState();
    // 渲染後取得一般獎項圖片的 url
    useEffect(() => {
        axios.get('http://127.0.0.1:5000/fashion-box-img-url')
            .then((response) => {
                // url list 存在 itemUrlData
                setItemUrl(response.data.item_info);
            })
    }, []);

    // 取得不存在獎項的 img url
    const notAwardList = ['時裝神奇剪刀', '超性能擴音器', '核心寶石1個交換券(不可交易)', '核心寶石1個交換券', '日拋隱形眼鏡',
        '通用高級染髮券', '通用高級護膚券', '通用高級整形券', '通用整形券',
        '通用高級美髮券', '通用美髮券', '透明耳環', '透明鞋子', '透明手套',
        '透明披風', '透明眼部裝飾', '透明面具', '透明帽'];
    // 儲存 api 抽出來的物品
    const [item, setItem] = useState(null);
    // 儲存目前中獎物品列表
    const [awardList, setAwardList] = useState([]);
    // 儲存目前總抽數
    const [count, setCount] = useState(0);
    // 控制 Modal
    const [show, setShow] = useState(false);
    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);
    // 控制 Modal 抽獎字串
    const [showContent, setshowContent] = useState();
    const [showLinkText, setShowLinkText] = useState();

    // 渲染後先執行抽獎動作一次 => 避免第一次抽獎拿不到資料顯示 Null
    useEffect(() => {
        axios.get('http://127.0.0.1:5000/draw-fashion-box')
            .then((response) => {
                // 不是 json 直接拿 response.data 
                setItem(response.data);
            })
    }, []);
    // 小視窗彈出來後按抽獎
    const handleDrawFashionBoxClick = () => {
        axios.get('http://127.0.0.1:5000/draw-fashion-box')
            .then((response) => {
                // 不是 json 直接拿可以 response.data 
                // 從 api 取得抽出的物品存在 item
                setItem(response.data);
            })

        if (boxCount > 0) {
            setCount(count + 1);
            dispatch(drawBox());
            let imgUrl = itemUrlData.find(img => img.item_name === item);
            // 新的時裝查不到圖片網址
            if (!imgUrl) { imgUrl = `[new]`; }
            const htmlContent = `如何？<img src='${imgUrl.item_url}'
        ></img><span className='text-danger'>${item}</span>拿到道具了嗎？不覺得很神奇嗎？下次再有
        <span className='text-primary'>時尚隨機箱卷</span>的時候找我吧！<br/>`;
            const reactElement = parse(htmlContent);
            setshowContent(reactElement);
            setShowLinkText('再抽一次');
            if (!notAwardList.includes(item)) {
                setAwardList(getAwardList => [...getAwardList, item]);
            }
        } else {
            console.log('cant draw!');
            handleClose();
        }
    }

    return (
        <DrawModal
            show={show}
            showContent={showContent}
            showLinkText={showLinkText}
            onLinkClick={handleDrawFashionBoxClick()}
        />
    )
}
