import './App.css';
import React , {useState} from "react";
import { Navbar, NavDropdown, Container } from 'react-bootstrap';
import { FashionBox, HandleClickFashionBox } from './components/fashion-box/fashion-box-redux';
import ChatBox from './components/chat-box/chat-box';

function App() {
    const [showDropdown, setShowDropdown] = useState(false);

    const handleMouseEnter = () => {
        setShowDropdown(true);
    };

    const handleMouseLeave = () => {
        setShowDropdown(false);
    };

    return (
        <body>
            <Navbar bg="dark" data-bs-theme="dark">
                <Container>
                    <Navbar.Brand href="http://localhost:3000/"><b>楓之谷轉蛋模擬器</b></Navbar.Brand>
                    <Navbar.Text className="justify-content-end">
                        <NavDropdown title="目前累積" id="basic-nav-dropdown"
                            show={showDropdown}
                            onMouseEnter={handleMouseEnter}
                            onMouseLeave={handleMouseLeave}>
                            <NavDropdown.Item href="#action/3.1">Action</NavDropdown.Item>
                            <NavDropdown.Item href="#action/3.2">
                                Another action
                            </NavDropdown.Item>
                            <NavDropdown.Item href="#action/3.3">Something</NavDropdown.Item>
                            <NavDropdown.Divider />
                            <NavDropdown.Item href="#action/3.4">
                                Separated link
                            </NavDropdown.Item>
                        </NavDropdown>
                    </Navbar.Text>
                </Container>
            </Navbar>
            {/* <Navbar bg="secondary" data-bs-theme="dark">
                <Container>
                    <Navbar.Text className="justify-content-start">
                        時尚隨機箱
                    </Navbar.Text>
                    <Navbar.Text className="justify-content-end">
                        共花費{ }元
                    </Navbar.Text>
                </Container>
            </Navbar> */}
            <div className='store-container'>
                <div className='buy-box'>
                    <FashionBox />
                </div>
                <div className='buy-box'>
                    <HandleClickFashionBox />
                </div>
                <div className='chat-box'>
                    <ChatBox />
                </div>

            </div>
            <div className="footer">
                This is the footer content.
            </div>

        </body>
    )
}

export default App;
