import { createSlice } from '@reduxjs/toolkit'

export const buyBoxSlice = createSlice({
    name: 'buyBox',
    initialState: {
        boxCount: 0,
        totalPrice: 0
    },
    reducers: {
        buyBox: (state) => {
            state.boxCount += 10
            state.totalPrice += 270
        }
    },
})

// Action creators are generated for each case reducer function
export const { buyBox } = buyBoxSlice.actions
export default buyFashionBoxSlice.reducer
