import { createSlice } from '@reduxjs/toolkit'

export const buyFashionBoxSlice = createSlice({
    name: 'buyFashionBox',
    initialState: {
        boxCount: 0,
        totalPrice: 0
    },
    reducers: {
        buyBox: (state) => {
            state.boxCount += 10,
            state.totalPrice += 270
        }
    },
})

// Action creators are generated for each case reducer function
export const  buyBox = buyFashionBoxSlice.actions
export default buyFashionBoxSlice.reducer
