import './App.css';
import React, { useState } from "react";
import { Navbar, NavDropdown, Container } from 'react-bootstrap';
import { FashionBox, HandleClickFashionBox } from './components/fashion-box/fashion-box-redux';
import ChatBox from './components/chat-box/chat-box';

import { useSelector, useDispatch } from 'react-redux';

function App() {
    const fashionBoxCount = useSelector((state) => state.buyFashionBox.count);

    const [showDropdown, setShowDropdown] = useState(false);

    const handleToggleDropdown = () => {
        setShowDropdown(!showDropdown);
    };

    return (
        <body>
            <Navbar bg="dark" data-bs-theme="dark">
                <Container>
                    <Navbar.Brand href="http://localhost:3000/"><b>楓之谷轉蛋模擬器</b></Navbar.Brand>
                    <Navbar.Text className="justify-content-end">
                        <NavDropdown title="目前累積" id="basic-nav-dropdown"
                            show={showDropdown}
                            onMouseEnter={handleToggleDropdown}
                            onClick={handleToggleDropdown}
                        >
                            <NavDropdown.Item>時尚隨機箱</NavDropdown.Item>
                            <NavDropdown.Item>寵物隨機箱</NavDropdown.Item>
                            <NavDropdown.Item> Something</NavDropdown.Item>
                            <NavDropdown.Divider />
                            <NavDropdown.Item>
                                總金額：
                            </NavDropdown.Item>
                        </NavDropdown>
                    </Navbar.Text>
                </Container>
            </Navbar>
            {/* <Navbar bg="secondary" data-bs-theme="dark">
                <Container>
                    <Navbar.Text className="justify-content-start">
                        時尚隨機箱
                    </Navbar.Text>
                    <Navbar.Text className="justify-content-end">
                        共花費{ }元
                    </Navbar.Text>
                </Container>
            </Navbar> */}
            <div className='store-container'>
                <div className='buy-box'>
                    <FashionBox />
                </div>
                <div className='buy-box'>
                    <HandleClickFashionBox />
                </div>
                <div className='chat-box'>
                    <ChatBox />
                </div>

            </div>
            <div className="footer">
                This is the footer content.
            </div>

        </body>
    )
}

export default App;
